mapper.select:
Select pc.process_name,pc.process_name_en,pro.project_name,s.status_name,p.contract_no,p.grade,p.od,p.wt,p.p_length,p.weight,p.heat_no,o.* from inspection_process_record_header o inner join process_info pc on pc.process_code=o.process_code left join pipe_basic_info p on p.pipe_no=o.pipe_no left join contract_info c on p.contract_no=c.contract_no left join project_info pro on c.project_no=pro.project_no left join pipe_status s on s.status_code=p.status
AND o.pipe_no LIKE CONCAT('%',#{pipe_no},'%')
AND o.operator_no LIKE CONCAT('%',#{operator_no},'%')
AND o.process_code=#{process_code}
AND o.mill_no =#{mill_no}
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%T:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d 00:00:00')
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%T:%s')<DATE_FORMAT(DATE_ADD(#{end_time},INTERVAL 1 DAY),'%Y-%m-%d 00:00:00')
ORDER BY o.id DESC limit #{skip},#{take}

SELECT count(DISTINCT(o.pipe_no)) from inspection_process_record_header o
left join pipe_basic_info p on p.pipe_no=o.pipe_no
left join contract_info c on p.contract_no=c.contract_no
left join project_info pro on c.project_no=pro.project_no

    AND o.process_code='od_final_inspection'

        AND pro.project_no=#{project_no}


        AND o.mill_no=#{mill_no}


        AND c.external_coating=#{external_coating}


        AND c.internal_coating=#{internal_coating}

    AND format(c.od,3)=format(#{od},3)
    AND format(c.wt,3)=format(#{wt},3)

         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT count(DISTINCT(o.pipe_no)) as odtotalcount,SUM(p.p_length) as odtotallength from inspection_process_record_header o
left join pipe_basic_info p on p.pipe_no=o.pipe_no
left join contract_info c on p.contract_no=c.contract_no
left join project_info pro on c.project_no=pro.project_no

    AND o.process_code='od_final_inspection'
    AND c.external_coating=#{external_coating}
    AND c.internal_coating=#{internal_coating}
    AND pro.project_no=#{project_no}
    AND format(c.od,3)=format(#{od},3)
    AND format(c.wt,3)=format(#{wt},3)
    AND o.result=#{result}

         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT count(DISTINCT(o.pipe_no)) from inspection_process_record_header o
inner join inspection_process_record_item rditem on rditem.inspection_process_record_header_code= o.inspection_process_record_header_code
left join pipe_basic_info p on p.pipe_no=o.pipe_no
left join contract_info c on p.contract_no=c.contract_no
left join project_info pro on c.project_no=pro.project_no


    AND o.process_code='coating_repair'

        AND  rditem.item_code='repair_odid'
        AND  rditem.item_value=#{odid}


        AND  pro.project_no=#{project_no}


        AND o.mill_no=#{mill_no}



        AND c.external_coating=#{external_coating}


        AND  c.internal_coating=#{internal_coating}

    AND format(c.od,3)=format(#{od},3)
    AND format(c.wt,3)=format(#{wt},3)

         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT count(DISTINCT(o.pipe_no)) from inspection_process_record_header o left join pipe_basic_info p on p.pipe_no=o.pipe_no left join contract_info c on p.contract_no=c.contract_no left join project_info pro on c.project_no=pro.project_no
AND (o.process_code='od_blast' OR o.process_code='od_blast_inspection' OR o.process_code='od_coating_inspection' OR o.process_code='od_final_inspection' )
AND o.result='20'
AND pro.project_no=#{project_no}
AND o.mill_no=#{mill_no}
AND c.external_coating=#{external_coating}
AND c.internal_coating=#{internal_coating}
AND format(c.od,3)=format(#{od},3) AND format(c.wt,3)=format(#{wt},3)
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT count(DISTINCT(o.pipe_no)) from inspection_process_record_header o left join pipe_basic_info p on p.pipe_no=o.pipe_no left join contract_info c on p.contract_no=c.contract_no left join project_info pro on c.project_no=pro.project_no
AND (o.process_code='id_blast' OR o.process_code='id_blast_inspection' OR o.process_code='id_coating_inspection' OR o.process_code='id_final_inspection' )
AND o.result='20'
AND pro.project_no=#{project_no}
AND o.mill_no=#{mill_no}
AND c.external_coating=#{external_coating}
AND c.internal_coating=#{internal_coating}
AND format(c.od,3)=format(#{od},3) AND format(c.wt,3)=format(#{wt},3)
