AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT count(DISTINCT(o.pipe_no)) from inspection_process_record_header o inner join inspection_process_record_item rditem on rditem.inspection_process_record_header_code= o.inspection_process_record_header_code left join pipe_basic_info p on p.pipe_no=o.pipe_no left join contract_info c on p.contract_no=c.contract_no left join project_info pro on c.project_no=pro.project_no
AND o.result='1' AND o.process_code='grinding_cutoff'
AND o.inspection_process_record_header_code in (select inspection_process_record_header_code from inspection_process_record_item where item_code='grinding_cutoff' and item_value LIKE '%Grinding%')
AND o.inspection_process_record_header_code in (select inspection_process_record_header_code from inspection_process_record_item where item_code='grinding_cutoff_odid' and item_value =#{odid} )
AND pro.project_no=#{project_no}
AND o.mill_no=#{mill_no}
AND c.external_coating=#{external_coating}
AND c.internal_coating=#{internal_coating}
AND format(c.od,3)=format(#{od},3) AND format(c.wt,3)=format(#{wt},3)
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

