    AND o.process_code='id_final_inspection'

        AND c.external_coating=#{external_coating}


        AND  c.internal_coating=#{internal_coating}


        AND  pro.project_no=#{project_no}

    AND format(c.od,3)=format(#{od},3)
    AND format(c.wt,3)=format(#{wt},3)
    AND o.result=#{result}

         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT o.*,rditem.*, SUM(CASE item_code WHEN 'sample_cutoff_length' THEN item_value END) AS 'sample_cutoff_length', SUM(CASE item_code WHEN 'sample_pipe_cutoff_length' THEN item_value END) AS 'sample_pipe_cutoff_length', SUM(CASE item_code WHEN 'pipe_length_before_cut' THEN item_value END) AS 'pipe_length_before_cut' from inspection_process_record_header o inner join inspection_process_record_item rditem on rditem.inspection_process_record_header_code= o.inspection_process_record_header_code left join pipe_basic_info p on p.pipe_no=o.pipe_no left join contract_info c on p.contract_no=c.contract_no left join project_info pro on c.project_no=pro.project_no
AND o.result='1' AND o.process_code='coating_sampling' AND ( rditem.item_code='sample_cutoff_length' or rditem.item_code='sample_pipe_cutoff_length' or rditem.item_code='pipe_length_before_cut')
AND c.external_coating=#{external_coating}
AND c.internal_coating=#{internal_coating}
AND pro.project_no=#{project_no}
AND format(c.od,3)=format(#{od},3) AND format(c.wt,3)=format(#{wt},3)
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

