SELECT count(DISTINCT(sh.pipe_no)) as totalcount, sum(p.p_length) as totallength from shipment_info sh
inner join pipe_basic_info p on p.pipe_no=sh.pipe_no
inner join contract_info c on c.contract_no=p.contract_no



        AND c.project_no=#{project_no}


        AND c.external_coating=#{external_coating}


        AND c.internal_coating=#{internal_coating}

    AND format(c.od,3)=format(#{od},3)
    AND format(c.wt,3)=format(#{wt},3)

         AND DATE_FORMAT(sh.shipment_date,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')


         AND DATE_FORMAT(sh.shipment_date,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

(select p.odsampling_mark, rditem.item_code as testing_type,h.id,h.operation_time,h.pipe_no as pipe_no, c.external_coating,c.internal_coating, null as testing_result from inspection_process_record_header h inner join inspection_process_record_item rditem on rditem.inspection_process_record_header_code= h.inspection_process_record_header_code inner join pipe_basic_info p on p.pipe_no=h.pipe_no inner join contract_info c on p.contract_no=c.contract_no inner join project_info pro on c.project_no=pro.project_no
AND h.result='1' AND (h.process_code='od_coating_inspection' or h.process_code='id_coating_inspection') AND rditem.item_value='1' AND (rditem.item_code='is_sample' or rditem.item_code='is_dsc_sample' or rditem.item_code='is_pe_sample' or rditem.item_code='is_steel_sample' or rditem.item_code='is_glass_sample' )
AND pro.project_no=#{project_no}
)
union ( select '' as odsampling_mark, hd.process_code as testing_type,hd.id,hd.operation_time,hd.pipe_no as pipe_no, c.external_coating,c.internal_coating, hd.result as testing_result from inspection_process_record_header hd inner join pipe_basic_info p on p.pipe_no=hd.pipe_no inner join contract_info c on p.contract_no=c.contract_no inner join project_info pro on c.project_no=pro.project_no
AND (hd.process_code ='lab_testing_raw_material_3LPE' or hd.process_code ='lab_testing_raw_material_2FBE' or hd.process_code ='lab_testing_raw_material_EPOXY')
AND pro.project_no=#{project_no}
)
order by operation_time desc limit #{skip},#{take}

