mapper.select:
SELECT DISTINCT(o.pipe_no),o.pipe_no,s.cut_off_length from lab_testing_2fbe o LEFT JOIN pipe_sampling_record s ON o.pipe_no=s.pipe_no left join pipe_basic_info p on p.pipe_no=o.pipe_no left join contract_info c on p.contract_no=c.contract_no left join project_info pro on c.project_no=pro.project_no
AND pro.project_no=#{project_no}
AND s.mill_no=#{mill_no}
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')
AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT  * from lab_testing_2fbe


    OR  pipe_no=#{pipe_no}
    OR  dsc_pipe_no=#{pipe_no}



order by operation_time desc LIMIT 1

(SELECT 'LAB2FBE' as testing_type,o.id,o.result, 'REGULAR' as sample_type,o.operation_time from lab_testing_2fbe o
LEFT join pipe_basic_info p on p.pipe_no=o.pipe_no
LEFT join contract_info c on p.contract_no=c.contract_no
LEFT join project_info pro on c.project_no=pro.project_no


        AND pro.project_no=#{project_no}


)

UNION
(SELECT  'LAB2FBE' as testing_type,o.id,o.result, 'DSC' as sample_type,o.operation_time   from lab_testing_2fbe o
LEFT join pipe_basic_info p on p.pipe_no=o.dsc_pipe_no
LEFT join contract_info c on p.contract_no=c.contract_no
LEFT join project_info pro on c.project_no=pro.project_no


        AND pro.project_no=#{project_no}


)
UNION
(SELECT  'LAB3LPE' as testing_type,o.id,o.result, 'REGULAR' as sample_type,o.operation_time   from lab_testing_3lpe o
LEFT join pipe_basic_info p on p.pipe_no=o.pipe_no
LEFT join contract_info c on p.contract_no=c.contract_no
LEFT join project_info pro on c.project_no=pro.project_no


        AND pro.project_no=#{project_no}


)
UNION
(SELECT  'LAB3LPE' as testing_type,o.id,o.result, 'DSC' as sample_type,o.operation_time   from lab_testing_3lpe o
LEFT join pipe_basic_info p on p.pipe_no=o.dsc_pipe_no
LEFT join contract_info c on p.contract_no=c.contract_no
LEFT join project_info pro on c.project_no=pro.project_no


        AND pro.project_no=#{project_no}


)
UNION
(SELECT  'LAB3LPE' as testing_type,o.id,o.result, 'PE' as sample_type,o.operation_time   from lab_testing_3lpe o
LEFT join pipe_basic_info p on p.pipe_no=o.pe_pipe_no
LEFT join contract_info c on p.contract_no=c.contract_no
LEFT join project_info pro on c.project_no=pro.project_no


        AND pro.project_no=#{project_no}


)
UNION
(SELECT  'LABEPOXY' as testing_type,o.id,o.result, 'REGULAR' as sample_type,o.operation_time   from lab_testing_liquid_epoxy o
LEFT join pipe_basic_info p on p.pipe_no=o.pipe_no
LEFT join contract_info c on p.contract_no=c.contract_no
LEFT join project_info pro on c.project_no=pro.project_no


        AND pro.project_no=#{project_no}


)
UNION
(SELECT  'LABEPOXY' as testing_type,o.id,o.result, 'GLASS' as sample_type,o.operation_time   from lab_testing_liquid_epoxy o
LEFT join pipe_basic_info p on p.pipe_no=o.glass_pipe_no
LEFT join contract_info c on p.contract_no=c.contract_no
LEFT join project_info pro on c.project_no=pro.project_no


        AND pro.project_no=#{project_no}


)
UNION
(SELECT  'RAW2FBE' as testing_type,o.id,o.result, 'REGULAR' as sample_type,o.operation_time   from raw_material_testing_2fbe o


        AND project_no=#{project_no}


)
UNION
(SELECT  'RAW3LPE' as testing_type,o.id,o.result, 'REGULAR' as sample_type,o.operation_time   from raw_material_testing_3lpe o


        AND project_no=#{project_no}


)
UNION
(SELECT  'RAWEPOXY' as testing_type,o.id,o.result, 'REGULAR' as sample_type,o.operation_time   from raw_material_testing_liquid_epoxy o


        AND project_no=#{project_no}


)
order by operation_time desc
limit #{skip},#{take}
