mapper.select:
SELECT count(*) as odtotalcount,SUM(p.p_length) as odtotallength from od_final_inspection_process o
left join pipe_basic_info p on p.pipe_no=o.pipe_no
left join contract_info c on p.contract_no=c.contract_no
left join project_info pro on c.project_no=pro.project_no

    c.external_coating=#{external_coating}
    AND c.internal_coating=#{internal_coating}
    AND pro.project_no=#{project_no}
    AND c.od=#{od}
    and c.wt=#{wt}
    AND o.result=#{result}

         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<=DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT count(*) from od_final_inspection_process o
left join pipe_basic_info p on p.pipe_no=o.pipe_no
left join contract_info c on p.contract_no=c.contract_no
left join project_info pro on c.project_no=pro.project_no


        pro.project_no=#{project_no}


        o.mill_no=#{mill_no}


        c.external_coating=#{external_coating}


        c.internal_coating=#{internal_coating}


        c.od=#{od}


        c.wt=#{wt}


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<=DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')

SELECT count(DISTINCT(o.pipe_no)) from id_final_inspection_process o
left join pipe_basic_info p on p.pipe_no=o.pipe_no
left join contract_info c on p.contract_no=c.contract_no
left join project_info pro on c.project_no=pro.project_no

    o.result='1'

        pro.project_no=#{project_no}


        o.mill_no=#{mill_no}


        c.external_coating=#{external_coating}


        c.internal_coating=#{internal_coating}


        c.od=#{od}


        c.wt=#{wt}


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')>=DATE_FORMAT(#{begin_time},'%Y-%m-%d %H:%i:%s')


         AND DATE_FORMAT(o.operation_time,'%Y-%m-%d %H:%i:%s')<=DATE_FORMAT(#{end_time},'%Y-%m-%d %H:%i:%s')
