mapper.select:
select co.project_no,co.external_coating,co.internal_coating, project_name, p.* from pipe_basic_info p left join contract_info co on p.contract_no=co.contract_no left join project_info pr on co.project_no=pr.project_no
AND co.project_no LIKE CONCAT('%',#{project_no},'%')
AND p.contract_no LIKE CONCAT('%',#{contract_no},'%')
AND pipe_no LIKE CONCAT('%',#{pipe_no},'%')
AND p.status = #{status}
ORDER BY id DESC limit #{skip},#{take}

select count(p.id) from od_blast_process o inner join pipe_basic_info p on p.pipe_no=o.pipe_no inner join pipe_status s on s.status_code=p.status
AND p.id = #{id}
AND p.status <> 'odstrip2'

select count(p.id) from id_blast_process o inner join pipe_basic_info p on p.pipe_no=o.pipe_no inner join pipe_status s on s.status_code=p.status
AND p.id = #{id}
AND p.status <> 'idstrip2'
